import React, { useEffect, useState } from 'react';
import {
    Text,
    View,
    Image,
    Dimensions,
    TouchableOpacity
} from "react-native";
import { FlatList, TextInput } from 'react-native-gesture-handler';
import Icon from 'react-native-vector-icons/FontAwesome5';
import { SafeAreaView } from 'react-navigation';
import { UIHeader } from '../../components';
import { colors, fontSizes } from '../../constants';
import MessengerItem from './MessengerItem';

const Messenger = (props: any) => {
    const { url, name } = props.route.params.user;
    const { navigation } = props;
    const [typedText, setTypedText] = useState('');
    const [chatHistory, setChatHistory] = useState([
        {
            url: 'https://randomuser.me/api/portraits/men/70.jpg',
            showUrl: true,
            isSender: true,
            messenger: "Hello",
            timestamp: 1641654238000,
        },
        {
            url: 'https://randomuser.me/api/portraits/men/70.jpg',
            showUrl: false,
            isSender: true,
            messenger: "How are you ?",
            timestamp: 1641654298000,
        },
        {
            url: 'https://randomuser.me/api/portraits/men/70.jpg',
            showUrl: false,
            isSender: true,
            messenger: "How about your work ? How about your work ? How about your work ? How about your work ? How about your work ? How about your work ?",
            timestamp: 1641654538000,
        },
        {
            url: 'https://randomuser.me/api/portraits/men/50.jpg',
            showUrl: true,
            isSender: false,
            messenger: "Yes",
            timestamp: 1641654598000,
        },
        {
            url: 'https://randomuser.me/api/portraits/men/50.jpg',
            showUrl: false,
            isSender: false,
            messenger: "I am fine",
            timestamp: 1641654598000,
        },
        {
            url: 'https://randomuser.me/api/portraits/men/70.jpg',
            showUrl: true,
            isSender: true,
            messenger: "Let's go out",
            timestamp: 1641654778000,
        },
    ]);

    return (
        <View
            style={{
                flexDirection: 'column',
                flex: 1
            }}
        >
            <UIHeader
                title={name}
                lefIconName={'arrow-left'}
                rightIconName={'ellipsis-v'}
                onPressLeftIcon={() => {
                    navigation.goBack();
                }}
                onPressRightIcon={() => {
                    return alert('right icon');
                }}
            />
            <FlatList
                style={{
                    flex: 1
                }}
                keyExtractor={(item,) => item.messenger}
                data={chatHistory}
                renderItem={({ item, index }) =>
                    <MessengerItem
                        key={`${item.timestamp}`}
                        item={item}
                        onPress={() => alert("234")}
                    />}
            />
            <View style={{
                height: 50,
                position: 'absolute',
                bottom: 0,
                left: 0,
                right: 0,
                borderWidth: 1,
                borderTopColor: '#ccc',
                flexDirection: 'row',
                justifyContent: 'space-between',
                alignItems: 'center'
            }}>
                <TextInput
                    value={typedText}
                    onChangeText={(text) => {
                        setTypedText(text)
                    }}
                    placeholder='Enter your message here'
                    placeholderTextColor={colors.placeholder}
                    style={{
                        height: 50,
                        paddingStart: 10,
                        color: 'black',
                        flex: 1
                    }}
                />
                <TouchableOpacity
                    onPress={() => {
                        if (typedText.trim().length == 0) {
                            return
                        }

                        let newMessageObject = {
                            url: 'https://randomuser.me/api/portraits/men/70.jpg',
                            showUrl: true,
                            isSender: true,
                            messenger: typedText,
                            timestamp: (new Date).getTime(),
                        }
                        d
                    }}
                >
                    <Icon name='paper-plane' size={20} color={colors.primary} style={{
                        padding: 10
                    }} />
                </TouchableOpacity>
            </View>
        </View>
    )
}

export default Messenger
