import React, { useState } from 'react';
import {
    Text,
    View,
    Image,
    FlatList,
    TouchableOpacity
} from "react-native";
import Icon from 'react-native-vector-icons/FontAwesome5';

import { colors, fontSizes, dataProducts } from '../../constants';
import FiveStars from './FiveStars';
import GridItem from './GridItem';

function ProductGridView(props: any) {
    const [products, setProducts] = useState(dataProducts.products);
    return (
        <View
            style={{
                flex: 1,
                backgroundColor: 'white'
            }}
        >
            <FlatList
                style={{ marginTop: 5 }}
                keyExtractor={(eachProduct) => eachProduct.productName}
                data={products}
                numColumns={2}
                renderItem={({ item, index }) => <GridItem item={item} index={index} />}
            />
        </View >
    )
}

export default ProductGridView;
