import React, { useState } from 'react';
import { Text, View, Image, ImageBackground } from "react-native";
import { UIButton } from '../components';

import { icons, images } from '../constants';
function Welcome(props: any) {
    // let accountTypes = [
    //     'Influencer', 'Business', 'Individual'
    // ];

    const [accountTypes, setAccountType] = useState([
        {
            name: 'Influencer',
            isSelected: true
        },
        {
            name: 'Business',
            isSelected: false
        },
        {
            name: 'Individual',
            isSelected: false
        }
    ])

    return (
        <View style={{
            backgroundColor: 'white',
            flex: 100
        }}>
            <ImageBackground
                source={images.background}
                resizeMode='cover'
                style={{
                    flex: 100,
                    justifyContent: 'center'
                }}

            >
                <View
                    style={{
                        flexDirection: 'row',
                        flex: 20,
                    }}
                >
                    <View
                        style={{
                            flexDirection: 'row',
                            height: 50,
                            flex: 1,
                            justifyContent: "flex-start",
                            alignItems: "center"
                        }}
                    >
                        <Image
                            source={icons.fire}
                            style={{
                                width: 30,
                                height: 30,
                                marginStart: 10,
                                marginEnd: 5
                            }}
                        />
                        <Text
                            style={{
                                color: "#fff"
                            }}
                        >
                            YOURCOMPANY.CO
                        </Text>
                        <View style={{ flex: 1 }}></View>
                        <Image
                            source={icons.question}
                            style={{
                                width: 20,
                                height: 20,
                                marginEnd: 10,
                                tintColor: "#fff"
                            }}
                        />
                    </View>
                </View>
                <View
                    style={{
                        flex: 20,
                        justifyContent: 'center',
                        alignItems: 'center'
                    }}
                >
                    <Text
                        style={{
                            marginBottom: 7,
                            color: '#fff',
                            fontSize: 12
                        }}
                    >
                        Welcom to
                    </Text>
                    <Text
                        style={{
                            marginBottom: 7,
                            color: '#fff',
                            fontWeight: 'bold'
                        }}
                    >
                        COURCOMPANY.CO
                    </Text>
                    <Text
                        style={{
                            marginBottom: 7,
                            color: '#fff',
                            fontSize: 12
                        }}
                    >
                        Please select your account type
                    </Text>
                </View>
                <View
                    style={{
                        flex: 40,
                    }}
                >
                    {accountTypes.map((accountType, index, array) => {
                        return (
                            <UIButton
                                key={index}
                                isSelected={accountType.isSelected}
                                title={accountType.name}
                                onPress={() => {
                                    setAccountTypeaccountTypes.map((eachAccountType) => {
                                        return { ...eachAccountType, isSelected: eachAccountType.name == accountType.name }
                                    })
                                    setAccountType(newAccountTypes);
                                }}
                            />
                        )
                    })}
                </View>
                <View
                    style={{
                        flex: 20,
                        backgroundColor: 'green'
                    }}
                ></View>
            </ImageBackground>
        </View>
    )
}

export default Welcome
