import * as React from 'react';
import { Settings, ProductGridView, FoodList } from '../screens';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { fontSizes, colors } from '../constants';
import { View } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome5';

const Tab = createBottomTabNavigator();
const screenOptions = ({ route }: any) => ({
    headerShown: false,
    tabBarActiveTintColor: colors.primary,
    tabBarInActiveTintColor: colors.inactive,
    tabBarActiveBackgroupColor: colors.primary,
    tabBarIcon: ({ focused, color, size }) => {
        let screenName = route.name;
        return <Icon
            name={route.name == "ProductGridView" ? "align-center" :
                (route.name == "FoodList" ? "accusoft" : (
                    route.name == "Settings" ? "cogs" :
                        (route.name == "Profile" ? "user" :
                            (route.name == "Chat" ? "comment-dots" : ""))
                ))}
            size={20}
            color={focused ? colors.primary : colors.inactive} />
    }
})


function UITab({ route }: any) {
    return (
        <Tab.Navigator initialRouteName={'ProductGridView'} screenOptions={screenOptions}>
            <Tab.Screen
                name={'ProductGridView'}
                component={ProductGridView}
            />
            <Tab.Screen
                name={'FoodList'}
                component={FoodList}
            />
            <Tab.Screen
                name={'Settings'}
                component={Settings}
            />
        </Tab.Navigator>
    )
}

export default UITab;
