import React, { useState } from 'react';
import {
    Text,
    View,
    ScrollView,
    Switch,
    SafeAreaView
} from "react-native";
import { TouchableOpacity } from 'react-native-gesture-handler';
import Icon from 'react-native-vector-icons/FontAwesome5';
import { UIHeader } from '../components';

import { colors, fontSizes } from '../constants';
import { auth, firebaseDatabase, firebaseDatabaseRef, firebaseSet } from '../firebase/firebase';

function Settings({ navigation }: NativeStackHeaderProps) {
    const [isEnabledLockApp, setIsEnabledLockApp] = useState(true);
    const [isUseFingerprint, setIsUseFingerprint] = useState(false);
    const [isChangePassword, setIsChangePassword] = useState(true);


    return <SafeAreaView style={{
        flex: 1,
    }}>
        <UIHeader title="Settings" />
        <ScrollView>
            <View style={{
                height: 40,
                backgroundColor: 'rgba(0,0,0,0.2)',
                justifyContent: 'center'
            }}>
                <Text
                    style={{
                        color: 'red',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Common
                </Text>
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='globe' size={20}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Language
                </Text>
                <View style={{ flex: 1 }} />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}>
                    English
                </Text>
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>


            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='cloud' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Enviroment
                </Text>
                <View style={{ flex: 1 }} />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}>
                    Production
                </Text>
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>







            <View style={{
                height: 40,
                backgroundColor: 'rgba(0,0,0,0.2)',
                justifyContent: 'center'
            }}>
                <Text
                    style={{
                        color: 'red',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Account
                </Text>
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='phone' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Phone number
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='envelope' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Email
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>
            <TouchableOpacity
                onPress={() => {
                    auth.signOut();
                }}
                style={{
                    flexDirection: 'row',
                    paddingVertical: 10,
                    alignItems: 'center',
                    height: 40
                }}>
                <Icon name='sign-out-alt' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    sign out
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </TouchableOpacity>




            <View style={{
                height: 40,
                backgroundColor: 'rgba(0,0,0,0.2)',
                justifyContent: 'center'
            }}>
                <Text
                    style={{
                        color: 'red',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Sercurity
                </Text>
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='door-closed' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Lock ap in background
                </Text>
                <View style={{ flex: 1 }} />
                <Switch
                    trackColor={{ false: colors.inactive, true: colors.primary }}
                    thumbColor={isEnabledLockApp ? colors.primary : colors.inactive}
                    ios_backgroundColor={colors.primary}
                    onValueChange={() => {
                        setIsEnabledLockApp(!isEnabledLockApp);
                    }}
                    value={isEnabledLockApp}
                    style={{ marginEnd: 10 }}
                />
            </View>

            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='fingerprint' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Use fingerprint
                </Text>
                <View style={{ flex: 1 }} />
                <Switch
                    trackColor={{ false: colors.inactive, true: colors.primary }}
                    thumbColor={isUseFingerprint ? colors.primary : colors.inactive}
                    ios_backgroundColor={colors.primary}
                    onValueChange={() => {
                        setIsUseFingerprint(!isUseFingerprint);
                    }}
                    value={isUseFingerprint}
                    style={{ marginEnd: 10 }}
                />
            </View>

            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='lock' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Change password
                </Text>
                <View style={{ flex: 1 }} />
                <Switch
                    trackColor={{ false: colors.inactive, true: colors.primary }}
                    thumbColor={isChangePassword ? colors.primary : colors.inactive}
                    ios_backgroundColor={colors.primary}
                    onValueChange={() => {
                        setIsChangePassword(!isChangePassword);
                    }}
                    value={isChangePassword}
                    style={{ marginEnd: 10 }}
                />
            </View>




            <View style={{
                height: 40,
                backgroundColor: 'rgba(0,0,0,0.2)',
                justifyContent: 'center'
            }}>
                <Text
                    style={{
                        color: 'red',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Misc
                </Text>
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='file-alt' size={20}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Term of Services
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center',
                height: 40
            }}>
                <Icon name='passport' size={20}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Open source liciense
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>
        </ScrollView >
    </SafeAreaView >
}

export default Settings;
