import React, { useState } from 'react';
import { Text, View, Image, TouchableOpacity, TextInput, KeyboardAvoidingView, Platform } from "react-native";
import Icon from 'react-native-vector-icons/FontAwesome5';

import { images, colors, icons, fontSizes } from '../constants';

const Login = (props: any) => {
    return (
        <KeyboardAvoidingView
            behavior={Platform.OS === 'android' ? "padding" : "height"}
            style={{
                flex: 1,
                backgroundColor: 'white'
            }}
        >
            <View
                style={{
                    height: 200,
                    flexDirection: 'row',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                    flex: 35
                }}
            >
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h1,
                        fontWeight: 'bold',
                        width: '50%'
                    }}
                >
                    Already have an Account
                </Text>
                <Image
                    source={images.computer}
                    style={{
                        width: 120,
                        height: 120,
                        tintColor: colors.primary,
                        alignSelf: 'center'
                    }}
                />
            </View>
            <View
                style={{
                    flex: 25
                }}
            >
                <View
                    style={{
                        marginHorizontal: 15
                    }}
                >
                    <Text
                        style={{
                            fontSize: fontSizes.h6,
                            color: colors.primary
                        }}
                    >
                        Email:
                    </Text>
                    <TextInput
                        placeholder='example@gmail.com'
                        placeholderTextColor={colors.placeholder}
                        style={{
                            color: 'black',

                        }}
                    />
                    <View
                        style={{
                            height: 1,
                            backgroundColor: colors.primary,
                            width: '100%',
                            marginBottom: 10,
                            marginHorizontal: 15,
                            alignSelf: 'center'
                        }}
                    />
                </View>
                <View
                    style={{
                        marginHorizontal: 15,
                        marginVertical: 10
                    }}
                >
                    <Text
                        style={{
                            fontSize: fontSizes.h6,
                            color: colors.primary
                        }}
                    >
                        Password:
                    </Text>
                    <TextInput
                        placeholder='Enter you password'
                        placeholderTextColor={colors.placeholder}
                        secureTextEntry={true}
                        style={{
                            color: 'black'
                        }}
                    />
                </View>
            </View>
            <View
                style={{
                    flex: 15
                }}
            >
                <TouchableOpacity
                    style={{
                        backgroundColor: colors.primary,
                        justifyContent: 'center',
                        alignItems: 'center',
                        width: '50%',
                        alignSelf: 'center',
                        borderRadius: 17
                    }}
                    onPress={() => {
                        alert("Press Login");
                    }}
                >
                    <Text
                        style={{
                            padding: 8,
                            color: '#fff',
                            fontSize: fontSizes.h5
                        }}
                    >
                        Login
                    </Text>
                </TouchableOpacity>
                <TouchableOpacity
                    style={{
                        padding: 5
                    }}
                    onPress={() => {
                        alert("Press Register");
                    }}
                >
                    <Text
                        style={{
                            padding: 8,
                            color: colors.primary,
                            fontSize: fontSizes.h5,
                            alignSelf: 'center'
                        }}
                    >
                        New user? Register now
                    </Text>
                </TouchableOpacity>
            </View>
            <View
                style={{
                    flex: 25,
                }}
            >
                <View
                    style={{
                        height: 40,
                        flexDirection: 'row',
                        alignItems: 'center',
                        marginHorizontal: 20
                    }}
                >
                    <View style={{ height: 1, backgroundColor: 'black', flex: 1 }} />
                    <Text
                        style={{
                            padding: 8,
                            fontSize: fontSizes.h6,
                            color: 'black',
                            alignSelf: 'center',
                            fontWeight: 'bold',
                            marginHorizontal: 5
                        }}
                    >
                        User other methods?
                    </Text>
                    <View style={{ height: 1, backgroundColor: 'black', flex: 1 }} />
                </View>
                <View
                    style={{
                        flexDirection: 'row',
                        justifyContent: 'center'
                    }}
                >
                    <TouchableOpacity
                        style={{
                            marginHorizontal: 5
                        }}
                        onPress={() => {
                            alert("Press Login facebook");
                        }}
                    >
                        <Icon name='facebook-square' size={40} color={colors.facebook} />
                    </TouchableOpacity>
                    <TouchableOpacity
                        style={{
                            marginHorizontal: 5
                        }}
                        onPress={() => {
                            alert("Press Login google");
                        }}
                    >
                        <Icon name='google-plus' size={40} color={colors.google} />
                    </TouchableOpacity>
                </View>

            </View>
        </KeyboardAvoidingView>
    )
}
export default Login
