import React, { useState } from 'react';
import {
    Text,
    View,
    Image,
    TouchableOpacity,
    TextInput,
    ScrollView,
    FlatList
} from "react-native";
import Icon from 'react-native-vector-icons/FontAwesome5';

import { images, colors, icons, fontSizes, dataProducts } from '../../constants';
import { isValidEmail, isValidPassword } from '../../util/Validations';

function ProductGridView(props: any) {
    const [products, setProducts] = useState(dataProducts.products);
    return (
        <View
            style={{
                flex: 1,
                backgroundColor: 'white'
            }}
        >
            <FlatList
                keyExtractor={(eachProduct) => eachProduct.productName}
                data={products}
                numColumns={2}
                renderItem={({ item, index }) => {
                    return (
                        <View
                            style={{
                                backgroundColor: index % 2 == 0 ? 'green' : 'red',
                                flex: 0.5,
                                height: 200,
                                marginLeft: index % 2 == 0 ? 10 : 0,
                                marginRight: 10,
                                marginTop: index == 0 || index == 1 ? 10 : 5,
                                marginBottom: 0
                            }}
                        >
                            <Text>{item.productName}</Text>
                        </View>
                    )
                }}
            />
        </View>
    )
}

export default ProductGridView;
