import React, { useEffect, useState } from 'react';
import {
    Text,
    View,
    Image,
    TouchableOpacity,
    TextInput,
    KeyboardAvoidingView,
    Platform,
    Keyboard,
    ScrollView
} from "react-native";
import Icon from 'react-native-vector-icons/FontAwesome5';
import { UIHeader } from '../components';

import { images, colors, icons, fontSizes } from '../constants';
import { isValidEmail, isValidPassword } from '../util/Validations';

function Settings(props: any) {
    return <View style={{
        flex: 1,
    }}>
        <UIHeader title="Settings" />
        <ScrollView>
            <View style={{
                height: 40,
                backgroundColor: 'rgba(0,0,0,0.2)',
                justifyContent: 'center'
            }}>
                <Text
                    style={{
                        color: 'red',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Common
                </Text>
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center'
            }}>
                <Icon name='globe' size={20}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Language
                </Text>
                <View style={{ flex: 1 }} />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}>
                    English
                </Text>
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>


            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center'
            }}>
                <Icon name='cloud' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Enviroment
                </Text>
                <View style={{ flex: 1 }} />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}>
                    Production
                </Text>
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>


            <View style={{
                height: 40,
                backgroundColor: 'rgba(0,0,0,0.2)',
                justifyContent: 'center'
            }}>
                <Text
                    style={{
                        color: 'red',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Account
                </Text>
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center'
            }}>
                <Icon name='phone' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Phone number
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center'
            }}>
                <Icon name='envolope' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    Email
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>
            <View style={{
                flexDirection: 'row',
                paddingVertical: 10,
                alignItems: 'center'
            }}>
                <Icon name='sign-out-alt' size={16}
                    style={{ marginStart: 10 }}
                    color='black'
                />
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6,
                        paddingStart: 10
                    }}>
                    sign out
                </Text>
                <View style={{ flex: 1 }} />
                <Icon name='chevron-right' size={20}
                    style={{
                        marginEnd: 2,
                        paddingEnd: 10,
                        opacity: 0.5
                    }}
                    color='black'

                />
            </View>
        </ScrollView>
    </View>
}

export default Settings;
