import { RANDOM_FACTOR } from '@firebase/util';
import { NativeStackHeaderProps } from '@react-navigation/native-stack';
import React, { useEffect, useState } from 'react';
import {
    Text,
    View,
    Image,
    Dimensions
} from "react-native";
import { FlatList } from 'react-native-gesture-handler';
import Icon from 'react-native-vector-icons/FontAwesome5';
import { SafeAreaView } from 'react-navigation';
import { UIHeader } from '../../components';
import { fontSizes } from '../../constants';
import { get, child, firebaseDatabase, firebaseDatabaseRef } from '../../firebase/firebase';
import ChatItem from './ChatItem';


const Chat = ({ navigation }: NativeStackHeaderProps) => {

    const [users, setUsers] = useState([
        {
            // url: 'https://randomuser.me/api/portraits/men/10.jpg',
            // name: 'Đỗ Quốc Thưởng',
            // message: 'Hello, how are you ?',
            // numberOfUnreadMessages: 3
        }
    ]);

    useEffect(() => {
        const dbRef = firebaseDatabaseRef(firebaseDatabase);
        get(child(dbRef, 'users')).then((snapshot) => {
            if (snapshot.exists()) {
                let value = snapshot.val();
                setUsers(Object.values(value).map(eachObject => {
                    return {
                        url: `https://randomuser.me/api/portraits/men/${RANDOM_FACTOR}.jpg`,
                        name: eachObject.email,
                        email: eachObject.email,
                        accessToken: eachObject.accessToken,
                        numberOfUnreadMessages: 0
                    }
                }));

            } else {
                console.log('No data avaiable')
            }
        }).catch((error) => {
            console.log(`Cannot get users from Firebase: ${error}`);
        })
    }, []);
    return (
        <View
            style={{
                flexDirection: 'column'
            }}
        >
            <UIHeader
                title={'Notification'}
                lefIconName={'arrow-left'}
                rightIconName={'search'}
                onPressLeftIcon={() => {
                    return alert('left icon');
                }}
                onPressRightIcon={() => {
                    return alert('right icon');
                }}
            />
            <View
                style={{
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    paddingStart: 10
                }}
            >
                <Text
                    style={{
                        color: 'black',
                        fontSize: fontSizes.h6
                    }}>
                    6 unread messages
                </Text>
                <Icon name={'search'} size={23} color={'white'}
                    style={{
                        padding: 10
                    }}
                    onPress={() => alert('You Press delete')}
                />
            </View>
            <FlatList
                style={{
                    // backgroundColor: 'red'
                }}
                keyExtractor={item => item.url}
                data={users}
                renderItem={({ item, index }) =>
                    <ChatItem
                        key={index}
                        name={item.name}
                        url={item.url}
                        message={item.message}
                        numberOfUnreadMessages={item.numberOfUnreadMessages}
                        onPress={() => navigation.navigate('Messenger', { user: item })}
                    />}
            />
        </View>
    )
}

export default Chat
